import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class AppComponent {
  showSearch = false;

  onSearchClick(): void {
    this.showSearch = true;
  }

  onCloseSearch(): void {
    this.showSearch = false;
  }
}
