import { Component } from '@angular/core';

@Component({
selector: 'app-navbar',
templateUrl: './navbar.component.html',
styleUrls: ['./navbar.component.css']
})
export class NavbarComponent {
showSearch = false;
showUploadForm = false;



toggleSearch(): void {
this.showSearch = true;
document.body.style.overflow = 'hidden'; // to prevent scrolling while search bar is open
}

closeSearch(): void {
this.showSearch = false;
document.body.style.overflow = 'auto'; // to re-enable scrolling when search bar is closed
}

toggleUploadForm(): void {
this.showUploadForm = true;
document.body.style.overflow = 'hidden'; // to prevent scrolling while upload form is open
}

closeUploadForm(): void {
this.showUploadForm = false;
document.body.style.overflow = 'auto'; // to re-enable scrolling when upload form is closed
}



}
