import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent implements OnInit {
  username = 'johndoe';
  bio = 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Praesent malesuada libero ac enim fringilla, non pellentesque mauris pulvinar.';
  profileImage = 'https://via.placeholder.com/150x150';
  postCount = 0; 
  followersCount = 500;
  followingCount = 200;


  posts = [
    {
      username: 'johndoe',
      image: 'https://www.shutterstock.com/image-photo/happy-cheerful-young-woman-wearing-260nw-613759379.jpg',
      likes: 50
    },
    {
      username: 'janedoe',
      image: 'https://via.placeholder.com/150x150',
      likes: 40
    },
    {
      username: 'jimdoe',
      image: 'https://via.placeholder.com/150x150',
      likes: 30
    },
    {
      username: 'johndoe1',
      image: 'https://via.placeholder.com/150x150',
      likes: 20
    },
    {
      username: 'janedoe1',
      image: 'https://via.placeholder.com/150x150',
      likes: 10
    },
    {
      username: 'asd',
      image: 'https://via.placeholder.com/150x150',
      likes: 10
    }
  ];

  constructor() { }

  ngOnInit(): void {
    this.postCount = this.posts.length;
  }

}
