import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent implements OnInit {
  showSearch = false;

  constructor() { }

  ngOnInit(): void {
  }

  toggleSearch(): void {
    this.showSearch = !this.showSearch;
  }
}
